import api from "./api";

export const getPlays = async () => (await api.get("/plays")).data;
export const getPlayById = async (id) => (await api.get(`/plays/${id}`)).data;
export const savePlay = async (playData) => (await api.post("/plays", playData)).data;
export const updatePlay = async (id, playData) => (await api.put(`/plays/${id}`, playData)).data;
