import React, { useEffect, useState } from "react";
import { getPlays } from "../services/playService";
import GlassLayout from "../components/GlassLayout";

export default function Dashboard() {
  const [plays, setPlays] = useState([]);

  useEffect(() => {
    async function load() {
      const data = await getPlays();
      setPlays(data);
    }
    load();
  }, []);

  return (
    <GlassLayout>
      <h1 className="text-4xl font-bold mb-6 text-blue-300">
        Your Playbook
      </h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {plays.map((play) => (
          <div
            key={play.id}
            className="
              bg-[#0a1a2f]/30 
              backdrop-blur-lg 
              border border-white/10 
              rounded-2xl p-5 
              hover:bg-[#11243d]/40 
              transition-all shadow-lg
            "
          >
            <h2 className="text-xl font-semibold">{play.name}</h2>
            <p className="text-white/60 text-sm mt-2">
              Last updated: {new Date(play.updatedAt).toLocaleDateString()}
            </p>
          </div>
        ))}
      </div>
    </GlassLayout>
  );
}
