import React, { useEffect, useState } from "react";
import { getPlays } from "../services/playService";

/**
 * Dashboard: lists plays with navy glass cards
 */
export default function Dashboard() {
  const [plays, setPlays] = useState([]);

  useEffect(() => {
    async function fetchPlays() {
      const data = await getPlays();
      setPlays(data);
    }
    fetchPlays();
  }, []);

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-[#020617] via-[#0a1a2f] to-black">
      <h1 className="text-4xl font-bold text-white mb-6">Dashboard</h1>

      <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {plays.map((play) => (
          <div key={play.id} className="glass-modal cursor-pointer hover:scale-105">
            <h2 className="text-xl font-semibold">{play.name}</h2>
            <p className="text-gray-300 text-sm">{play.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
