import React, { useState } from "react";
import axios from "axios";

/**
 * AuthPage:
 * Handles Login and Signup modals in a navy dark glass look.
 */
export default function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const API_BASE = "http://localhost:5001/api/auth";

  const validateEmail = (email) => /\S+@\S+\.\S+/.test(email);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!validateEmail(email)) return setError("Enter a valid email.");
    if (password.length < 6) return setError("Password must be 6+ chars.");

    try {
      setLoading(true);
      if (isLogin) {
        const res = await axios.post(`${API_BASE}/login`, { email, password });
        localStorage.setItem("token", res.data.token);
        // ✅ Redirect to dashboard
        window.location.href = "/dashboard";
        alert("Login successful!");      
      } else {
        await axios.post(`${API_BASE}/signup`, { name, email, password });
        setIsLogin(true);
        alert("Signup successful! Please log in.");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="glass-modal w-full max-w-md">
        <h2 className="text-3xl font-bold text-center mb-6">
          {isLogin ? "Welcome Back" : "Create Account"}
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          {!isLogin && (
            <input
              type="text"
              placeholder="Full Name"
              className="glass-input"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          )}
          <input
            type="email"
            placeholder="Email"
            className="glass-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="glass-input"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {error && <p className="text-red-400 text-center">{error}</p>}
          <button type="submit" disabled={loading} className="glass-btn w-full">
            {loading ? "Please wait..." : isLogin ? "Login" : "Sign Up"}
          </button>
        </form>

        <div className="text-center mt-6 text-gray-300 text-sm">
          {isLogin ? (
            <>
              Don’t have an account?{" "}
              <button onClick={() => setIsLogin(false)} className="text-blue-400 hover:underline">
                Sign up
              </button>
            </>
          ) : (
            <>
              Already have an account?{" "}
              <button onClick={() => setIsLogin(true)} className="text-blue-400 hover:underline">
                Log in
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
