import React, { useState } from "react";
import axios from "axios";

/**
 * AuthPage:
 * - Handles both Login and Signup modals
 * - New dark navy glassmorphism theme
 * - Strong client-side validation
 */
export default function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Backend URL
  const API_BASE = "http://localhost:5001/api/auth";

  const validateEmail = (email) => /\S+@\S+\.\S+/.test(email);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!validateEmail(email)) return setError("Please enter a valid email.");
    if (password.length < 6)
      return setError("Password must be at least 6 characters.");

    try {
      setLoading(true);

      if (isLogin) {
        // Login request
        const res = await axios.post(`${API_BASE}/login`, { email, password });
        localStorage.setItem("token", res.data.token);
        alert("Login successful!");
        window.location.href = "/";
      } else {
        // Signup request
        await axios.post(`${API_BASE}/signup`, { name, email, password });
        alert("Signup successful! Please log in.");
        setIsLogin(true);
      }
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || "Something went wrong.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="
        min-h-screen w-full flex items-center justify-center 
        bg-gradient-to-br from-[#020617] via-[#0a1a2f] to-black
        text-white px-4
      "
    >
      {/* Glass Card */}
      <div
        className="
          w-full max-w-md p-10 rounded-3xl shadow-2xl
          backdrop-blur-xl bg-[#0a1a2f]/40 border border-white/10
        "
      >
        <h2 className="text-3xl font-bold text-center mb-6 tracking-wide">
          {isLogin ? "Welcome Back" : "Create Your Account"}
        </h2>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-5">
          {!isLogin && (
            <div>
              <label className="block text-sm mb-1">Full Name</label>
              <input
                type="text"
                className="
                  w-full p-3 rounded-lg bg-white/10 text-white
                  border border-white/20 placeholder-white/50
                  focus:outline-none focus:ring-2 focus:ring-blue-400/50
                "
                placeholder="Coach Brandon"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
          )}

          {/* Email */}
          <div>
            <label className="block text-sm mb-1">Email</label>
            <input
              type="email"
              className="
                w-full p-3 rounded-lg bg-white/10 text-white
                border border-white/20 placeholder-white/50
                focus:outline-none focus:ring-2 focus:ring-blue-400/50
              "
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          {/* Password */}
          <div>
            <label className="block text-sm mb-1">Password</label>
            <input
              type="password"
              className="
                w-full p-3 rounded-lg bg-white/10 text-white
                border border-white/20 placeholder-white/50
                focus:outline-none focus:ring-2 focus:ring-blue-400/50
              "
              placeholder="••••••••"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          {/* Error */}
          {error && (
            <p className="text-red-400 text-sm text-center mt-2">{error}</p>
          )}

          {/* Submit Button */}
          <button
            type="submit"
            disabled={loading}
            className="
              w-full py-3 rounded-lg 
              bg-blue-600 hover:bg-blue-700 
              transition-all duration-300 font-semibold
              shadow-lg shadow-blue-500/20
              disabled:opacity-50 disabled:cursor-not-allowed
            "
          >
            {loading
              ? "Please wait..."
              : isLogin
              ? "Log In"
              : "Sign Up"}
          </button>
        </form>

        {/* Toggle Between Login / Signup */}
        <div className="text-center mt-6 text-gray-300 text-sm">
          {isLogin ? (
            <p>
              Don't have an account?{" "}
              <button
                className="text-blue-400 hover:underline"
                onClick={() => setIsLogin(false)}
              >
                Sign up
              </button>
            </p>
          ) : (
            <p>
              Already have an account?{" "}
              <button
                className="text-blue-400 hover:underline"
                onClick={() => setIsLogin(true)}
              >
                Log in
              </button>
            </p>
          )}
        </div>
      </div>
    </div>
  );
}
